import React, { Component } from 'react'
import Button from 'react-bootstrap/Button';
import './Header.sass';

class Header extends Component {

    render() {
        const image =`./hero_space.png`;
        return (
            <div id="home" className="App-header" style={headerStyle}>

                <h1 className="headerTitle">Software Development</h1>
                <div className="headerSubtitle">
                    <p className="subtitle">Turn your ideas into reality</p>
                </div>

                <Button className="contactButton" variant="primary" href='#contact'>
                    Contact
                </Button>

            </div>
        )
    }
}

const imageStyle={
    position: 'relative',
    display: 'flex',
    marginTop: '0px',
    width: '100%',
    height: '200px'
}

const headerStyle ={
    display: 'flex',
    width: '100%',
    height: '82vh',
    marginTop: '50px',
    background: `#FFF url(./head1a.jpg) no-repeat center`,
    backgroundSize: 'cover'
}

export default Header;
