import React, { Component } from 'react'
import Card from 'react-bootstrap/Card';
import PropTypes from 'prop-types';

class ServiceItem extends Component {
    render() {
    //    const image =`./${this.props.skill.image}`;
    const image ='./zic_wordpress.png';
        return (
          <Card style={cardStyle}>
            <Card.Body>
                <img src={image} style={icStyle}/>
              <Card.Title>{this.props.service.title}</Card.Title>
              <Card.Text>
                {this.props.service.description}
              </Card.Text>
            </Card.Body>
          </Card>
        )
    }
}

/*      <i className="material-icons-outlined" style={{fontSize: '84px', color: this.props.service.color}}>
                {this.props.service.image}</i>*/

const icStyle = {
  fontSize: '84px',
  width: '120px',
  marginBottom: '10px',
  color: '#5190f5'
}

const cardStyle = {
  width: '300px',
  height: '230px',
  alignSelf: 'center',
  marginTop: '16px'
}

ServiceItem.propTypes = {
  service: PropTypes.object.isRequired
}

export default ServiceItem;
